import React from 'react';
import { Grid, Dimmer, Loader } from 'semantic-ui-react';
import ModeHeader from './ModeHeader';
import StatsHeader from './StatsHeader';
import Player from './Player';

const Index = ({
    activeColumn,
    columns,
    players,
    removePlayer,
    handleModeChange,
    mode,
    loading
}) => {
    return (
        <Grid>
            <ModeHeader mode={mode} handleModeChange={handleModeChange} />

            <Dimmer.Dimmable
                as={<StatsHeader activeColumn={activeColumn} columns={columns} />}
                dimmed={loading}
                style={{ backgroundColor: '#2c3c57', color: 'white' }}
            >
                <Dimmer active={loading} />
                {/* <StatsHeader activeColumn={activeColumn} columns={columns} /> */}
            </Dimmer.Dimmable>

            {players.map((player, index) => (
                <Player
                    key={index}
                    columns={columns}
                    player={player}
                    mode={mode}
                    boardIndex={index}
                    removePlayer={removePlayer}
                />
            ))}
        </Grid>
    );
};

export default Index;

