import React from 'react';
import { Grid, Header } from 'semantic-ui-react';

const StatsHeader = ({ columns, activeColumnSort }) => {
    return (
        <Grid.Row columns={columns.length} stretched>
            {/* Rank header */}
            <Grid.Column textAlign="center" verticalAlign="middle" mobile="1">
                <Header size="tiny">
                    <Header.Subheader
                        as="h6"
                        style={{ color: activeColumnSort == '#' ? 'yellow' : '#92a2bd' }}
                    >
                        #
                    </Header.Subheader>
                </Header>
            </Grid.Column>

            {/* Stats headers */}
            {columns.map((column, index) => (
                <Grid.Column
                    className={index == 0 ? '' : 'compact'}
                    key={index}
                    onClick={() => {
                        console.log('yoyoyo');
                    }}
                    textAlign={index == 0 ? 'left' : 'center'}
                    verticalAlign="middle"
                    mobile={column.size}
                >
                    <Header size="tiny">
                        <Header.Subheader as="h6" style={{ color: '#92a2bd' }}>
                            {column.header}
                        </Header.Subheader>
                    </Header>
                </Grid.Column>
            ))}
        </Grid.Row>
    );
};

export default StatsHeader;
