import axios from 'axios';

const axiosInstance = axios.create({
    baseURL: `https://fortnite-public-api.theapinetwork.com/prod09`,
    timeout: 3000
});

export const getUsernames = async username => {
    const endPoint = `/users/search?username=${username}`;
    const response = await axiosInstance.get(endPoint);
    return response.data.entries;
};

export const getProfileByUsername = async (username, platform = 'pc', window = 'season7') => {
    let response = await axiosInstance.get(
        `/users/id?username=${username}&platform=${platform}&window=${window}`
    );

    const { uid } = response.data;

    response = await axiosInstance.get(`/users/public/br_stats`, {
        params: {
            user_id: uid,
            platform: platform,
            window: window
        }
    });
    return response.data;
};

export const getProfileByUid = async (uid, platform = 'pc', window = 'season7') => {
    const response = await axiosInstance.get(`/users/public/br_stats`, {
        params: {
            user_id: uid,
            platform: platform,
            window: window
        }
    });
    return response.data;
};

export const getNews = async (language = 'en') => {
    let response = await axiosInstance.get('/br_motd/get', {
        params: { language: language }
    });

    return response.data;
};
