import React, { useState } from 'react';
import { Grid, Dimmer, Loader } from 'semantic-ui-react';
import ModeHeader from './ModeHeader';
import StatsHeader from './StatsHeader';
import Player from './Player';

const menuStyle = {
    border: 'none',
    borderRadius: 0,
    boxShadow: 'none',
    marginBottom: '1em',
    marginTop: '4em',
    transition: 'box-shadow 0.5s ease, padding 0.5s ease'
};

const fixedMenuStyle = {
    backgroundColor: '#fff',
    border: '1px solid #ddd',
    boxShadow: '0px 3px 5px rgba(0, 0, 0, 0.2)'
};

const Index = ({ columns, players, removePlayer, handleModeChange, mode, loading }) => {
    const [menuFixed, setMenuFixed] = useState(false);

    stickTopMenu = () => setMenuFixed(true);

    unStickTopMenu = () => setMenuFixed(false);

    return (
        <React.Fragment>
            <Grid style={{ backgroundColor: '#2c3c57', color: 'white' }}>
                <ModeHeader mode={mode} handleModeChange={handleModeChange} />
                <StatsHeader columns={columns} />
            </Grid>
            {/* ******************************** */}
            <Visibility
                onBottomPassed={this.stickTopMenu}
                onBottomVisible={this.unStickTopMenu}
                once={false}
            >
                <Menu
                    borderless
                    fixed={menuFixed ? 'top' : undefined}
                    style={menuFixed ? fixedMenuStyle : menuStyle}
                >
                    <Container text>
                        <Menu.Item>
                            <Icon size="mini" name="coffee" />
                        </Menu.Item>
                        <Menu.Item header>Project Name</Menu.Item>
                        <Menu.Item as="a">Blog</Menu.Item>
                        <Menu.Item as="a">Articles</Menu.Item>

                        <Menu.Menu position="right">
                            <Dropdown text="Dropdown" pointing className="link item">
                                <Dropdown.Menu>
                                    <Dropdown.Item>List Item</Dropdown.Item>
                                    <Dropdown.Item>List Item</Dropdown.Item>
                                    <Dropdown.Divider />
                                    <Dropdown.Header>Header Item</Dropdown.Header>
                                    <Dropdown.Item>
                                        <i className="dropdown icon" />
                                        <span className="text">Submenu</span>
                                        <Dropdown.Menu>
                                            <Dropdown.Item>List Item</Dropdown.Item>
                                            <Dropdown.Item>List Item</Dropdown.Item>
                                        </Dropdown.Menu>
                                    </Dropdown.Item>
                                    <Dropdown.Item>List Item</Dropdown.Item>
                                </Dropdown.Menu>
                            </Dropdown>
                        </Menu.Menu>
                    </Container>
                </Menu>
            </Visibility>

            {/* ******************************** */}

            <Dimmer.Dimmable
                as={Grid}
                dimmed={loading}
                style={{ backgroundColor: '#2c3c57', color: 'white' }}
            >
                <Dimmer active={loading}>
                    <Loader>Loading...</Loader>
                </Dimmer>

                {players.map((player, index) => (
                    <Player
                        key={index}
                        columns={columns}
                        player={player}
                        mode={mode}
                        boardIndex={index}
                        removePlayer={removePlayer}
                    />
                ))}
            </Dimmer.Dimmable>
        </React.Fragment>
    );
};

export default Index;
