const SearchBar = async ({ handleResultSelect }) => {
    console.log('rendering');
    const [searchTerm, setSearchTerm] = useState('');
    const [results, setResults] = useState([]);
    const [isSearching, setIsSearching] = useState(false);
    const debouncedSearchTerm = useDebounce(await getUsernames(searchTerm), 500);

    useEffect(
        async () => {
            if (debouncedSearchTerm) {
                setIsSearching(true);
                const results = debouncedSearchTerm;
                const formattedResults = results.map((result, index) => {
                    result.key = index;
                    result.title = result.username;
                    return result;
                });
                setIsSearching(false);
                setResults(formattedResults);
            } else {
                setResults([]);
                // resetComponent();
            }
        },
        [debouncedSearchTerm] // Only call effect if debounced search term changes
    );

    const resetComponent = () => {
        setSearchTerm('');
        setIsSearching(false);
    };

    const onResultSelect = (e, { result }) => {
        setSearchTerm(result.username);
        handleResultSelect(result.username);
        // resetComponent();
    };

    const onSearchChange = async (e, { value }) => {
        setSearchTerm(value);
    };

    return (
        <Search
            input={{ fluid: true, icon: 'arrow circle right' }}
            loading={isSearching}
            placeholder={'Enter your Epic username'}
            onResultSelect={onResultSelect}
            onSearchChange={onSearchChange}
            results={results}
            resultRenderer={resultRenderer}
            value={searchTerm}
            showNoResults={false}
        />
    );
};

export default SearchBar;

// //////////////////////////////////////////////////////////
// Usage
function App() {
    const [searchTerm, setSearchTerm] = useState('');
    const [results, setResults] = useState([]);
    const [isSearching, setIsSearching] = useState(false);
    const debouncedSearchTerm = useDebounce(searchTerm, 500);

    // Effect for API call
    useEffect(
        () => {
            if (debouncedSearchTerm) {
                setIsSearching(true);
                searchCharacters(debouncedSearchTerm).then(results => {
                    setIsSearching(false);
                    setResults(results);
                });
            } else {
                setResults([]);
            }
        },
        [debouncedSearchTerm] // Only call effect if debounced search term changes
    );

    return (
        <div>
            <input
                placeholder="Search Marvel Comics"
                onChange={e => setSearchTerm(e.target.value)}
            />

            {isSearching && <div>Searching ...</div>}

            {results.map(result => (
                <div key={result.id}>
                    <h4>{result.title}</h4>
                    <img
                        src={`${result.thumbnail.path}/portrait_incredible.${
                            result.thumbnail.extension
                        }`}
                    />
                </div>
            ))}
        </div>
    );
}

// API search function
function searchCharacters(search) {
    const apiKey = 'f9dfb1e8d466d36c27850bedd2047687';
    return fetch(
        `https://gateway.marvel.com/v1/public/comics?apikey=${apiKey}&titleStartsWith=${search}`,
        {
            method: 'GET'
        }
    )
        .then(r => r.json())
        .then(r => r.data.results)
        .catch(error => {
            console.error(error);
            return [];
        });
}

// Hook
function useDebounce(value, delay) {
    // State and setters for debounced value
    const [debouncedValue, setDebouncedValue] = useState(value);

    useEffect(
        () => {
            // Update debounced value after delay
            const handler = _.debounce(setDebouncedValue(value), delay, {
                maxWait: 1000
            });
        },
        [value] // Only re-call effect if value or delay changes
    );

    return debouncedValue;
}
