import React, { Component } from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import _ from 'lodash';

import { Container, Grid, Icon } from 'semantic-ui-react';
import SearchBar from '../src/components/SearchBar';
import { fetchByUsername, getProfile } from '../src/actions';

import Board from '../src/components/board';

// show awards, i.e. most kills, most wins, highest kd etc etc in separate div

class Leaderboard extends Component {
    static async getInitialProps({ store, query, pathname, asPath, req }) {
        let { usernames } = query;

        if (!usernames) return { usernames: [] };

        usernames = typeof usernames == 'string' ? [usernames] : usernames;

        return { usernames };
    }

    constructor(props) {
        super(props);
        this.state = {
            players: [],
            mode: 'solo',
            columns: [],
            column: null,
            direction: null
        };
    }

    async componentDidMount() {
        const { usernames } = this.props;
        const { players } = this.state;
        let initialPlayers = [];
        this.initColumns();

        try {
            initialPlayers = await Promise.all(usernames.map(username => getProfile(username)));
        } catch (error) {
            console.log(error);
        }
        this.setState({
            players: [...players, ...initialPlayers]
        });

        const progressUpdater = this.getProgressUpdater();
        progressUpdater.setMaxTicks(10);
        axios
            .get('/api/property/documents.json')
            .then(res => {
                // save an object with the variable representation of the variable and the string
                // this will allow a forEach loop for teh promises of data
                let complianceStatus = {
                    needsUploading: 'needsUploading',
                    pendingApproval: 'pendingApproval',
                    approved: 'approved'
                };

                function filterer(status, target) {
                    return status === target;
                }

                for (let status in complianceStatus) {
                    progressUpdater.incrementMaxTicks(25);
                    let current = status;
                    Promise.map(res.data.compliance, record => {
                        return Promise.all([
                            filterer(record.type, complianceStatus[status]),
                            record
                        ]);
                    }).then(values => {
                        status = values
                            .filter(value => {
                                return value[0] == true;
                            })
                            .map(value => {
                                return value[1];
                            });
                        this.setState({ [complianceStatus[current]]: status });
                    });
                }

                progressUpdater.setMaxTicks(90);

                // general data
                Promise.map(res.data.general, record => {
                    return record;
                }).then(generalDocs => {
                    this.setState({ generalDocs });
                });
            })
            .then(() => {
                progressUpdater.setTick(100);
            })
            .catch(err => {
                console.log(err);
            });
    }

    addPlayer = async username => {
        const { players, column, mode, direction } = this.state;
        const newPlayer = await getProfile(username);
        let updatedPlayers = [...players, newPlayer];

        // if pre-sorted then apply sort setting to updated players
        if (column !== null) {
            updatedPlayers = this.sortPlayers(updatedPlayers, column, mode, direction);
        }

        this.setState({ players: updatedPlayers }, () => this.updateURL());
    };

    removePlayer = index => {
        const { players } = this.state;
        const updatedPlayers = players.filter((player, i) => i !== index);
        this.setState({ players: updatedPlayers }, () => this.updateURL());
    };

    changeMode = direction => {
        const { mode } = this.state;
        let nextMode = '';
        switch (mode) {
            case 'solo':
                nextMode = direction === 'forward' ? 'duo' : 'squad';
                break;
            case 'duo':
                nextMode = direction === 'forward' ? 'squad' : 'solo';
                break;
            case 'squad':
                nextMode = direction === 'forward' ? 'solo' : 'duo';
                break;
            default:
                break;
        }
        this.setState({ mode: nextMode });
    };

    handleSort = accessor => () => {
        const { column, players, direction, mode } = this.state;

        // Reverse order if direction set, else default to desc
        let sortDirection = direction === 'desc' ? 'asc' : 'desc';
        let sortedColumn = column;

        // Default to desc order on column switch
        if (column !== accessor) {
            // Default to asc order if username column
            sortDirection = accessor == 'username' ? 'asc' : 'desc';
            // Set sorted column name
            sortedColumn = accessor;
        }
        // Sort players
        const sortedPlayers = this.sortPlayers(players, accessor, mode, sortDirection);
        this.setState(
            {
                column: sortedColumn,
                players: sortedPlayers,
                direction: sortDirection
            },
            () => this.updateURL()
        );
    };

    sortPlayers = (players, accessor, mode, direction) => {
        return accessor == 'username'
            ? _.orderBy(this.state.players, [o => o.username.toLowerCase()], [direction])
            : _.orderBy(players, o => o.stats[`${accessor}_${mode}`], [direction]);
    };

    isPlayerSelected = username => {
        const { players } = this.state;
        return players.find(player => player.username === username);
    };

    updateURL = () => {
        const { router } = this.props;
        const { players } = this.state;
        const usernames = players.map(player => player.username);
        router.replace({
            pathname: '/leaderboard',
            query: { usernames: usernames },
            options: { shallow: true }
        });
    };

    initColumns = () => {
        this.setState({
            columns: [
                {
                    header: 'Player',
                    accessor: 'username',
                    sort: this.handleSort('username')
                },
                {
                    header: 'K/D',
                    accessor: 'kd',
                    sort: this.handleSort('kd')
                },
                {
                    header: 'Win %',
                    accessor: 'winrate',
                    sort: this.handleSort('winrate')
                },
                {
                    header: 'Kills',
                    accessor: 'kills',

                    sort: this.handleSort('kills')
                },
                {
                    header: 'Wins',
                    accessor: 'placetop1',
                    sort: this.handleSort('placetop1')
                }
            ]
        });
    };

    getProgressUpdater() {
        let maxTicks = 0;
        let currentTick = 0;
        let isTicking = false;

        const setTick = progress => {
            this.setState({ progress });
        };

        const tick = () => {
            setTick(++currentTick);
            if (currentTick < maxTicks) {
                isTicking = true;
                setTimeout(tick, 100);
            } else {
                isTicking = false;
            }
        };

        const setMaxTicks = progress => {
            maxTicks = progress;
            if (!isTicking) {
                tick();
            }
        };

        const incrementMaxTicks = increment => {
            maxTicks += increment;
            if (!isTicking) {
                tick();
            }
        };

        return {
            setMaxTicks,
            incrementMaxTicks,
            setTick
        };
    }

    render() {
        const { columns, mode, players } = this.state;
        const { searchResults, fetchByUsername } = this.props;
        const debouncedfetchByUsername = _.debounce(fetchByUsername, 500, {
            maxWait: 1000
        });
        return (
            <Container
                text
                textAlign="center"
                style={{
                    paddingTop: '100px',
                    paddingBottom: '100px',
                    height: '100%'
                }}
            >
                <h1>Board</h1>
                <Grid columns="1" centered textAlign="center">
                    <Grid.Column mobile="16" computer="6" largeScreen="6" tablet="6" widescreen="6">
                        <Container>
                            <SearchBar
                                checkDuplicateSelect={this.isPlayerSelected}
                                handleResultSelect={this.addPlayer}
                                fetchByUsername={debouncedfetchByUsername}
                                results={searchResults}
                            />
                        </Container>
                        <Icon name="share" />
                    </Grid.Column>
                    <Grid.Column textAlign="center">
                        <Board
                            columns={columns}
                            mode={mode}
                            players={players}
                            removePlayer={this.removePlayer}
                            handleModeChange={this.changeMode}
                        />
                    </Grid.Column>
                </Grid>
            </Container>
        );
    }
}

const mapStateToProps = state => {
    return {
        searchResults: state.search.searchResults
    };
};

const mapDispatchToProps = dispatch => bindActionCreators({ fetchByUsername }, dispatch);

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Leaderboard);
