import React, { useState } from 'react';
import {
    Grid,
    Dimmer,
    Loader,
    Container,
    Dropdown,
    Icon,
    Menu,
    Visibility,
    Rail,
    Sticky
} from 'semantic-ui-react';
import ModeHeader from './ModeHeader';
import StatsHeader from './StatsHeader';
import Player from './Player';

const menuStyle = {
    backgroundColor: '#2c3c57',
    color: 'white',
    border: 'none',
    borderRadius: 0,
    boxShadow: 'none',
    marginBottom: '1em',
    transition: 'box-shadow 0.5s ease, padding 0.5s ease'
};

const fixedMenuStyle = {
    backgroundColor: '#2c3c57',
    color: 'white',
    boxShadow: '0px 3px 5px rgba(0, 0, 0, 0.2)'
};

const Index = ({ columns, players, removePlayer, handleModeChange, mode, loading, contextRef }) => {
    const [menuFixed, setMenuFixed] = useState(false);
    // const [contextRef, setContextRef] = useState(null);
    const stickTopMenu = () => setMenuFixed(true);

    const unStickTopMenu = () => setMenuFixed(false);
    // const handleContextRef = contextRef => setContextRef(contextRef);

    return (
        <React.Fragment>
            {/* <Grid style={{ backgroundColor: '#2c3c57', color: 'white' }}>
                <ModeHeader mode={mode} handleModeChange={handleModeChange} />
                <StatsHeader columns={columns} />
            </Grid> */}
            {/* ******************************** */}
            <Sticky context={contextRef}>
                <Grid style={{ backgroundColor: '#2c3c57', color: 'white' }}>
                    <ModeHeader mode={mode} handleModeChange={handleModeChange} />
                    <StatsHeader columns={columns} />
                </Grid>
            </Sticky>
            {/* ******************************** */}

            {/* <Visibility
                as={Grid}
                onBottomPassed={stickTopMenu}
                onBottomVisible={unStickTopMenu}
                once={false}
            >
                <Menu
                    as={Grid}
                    borderless
                    fixed={menuFixed ? 'top' : undefined}
                    style={menuFixed ? fixedMenuStyle : menuStyle}
                >
                    <ModeHeader mode={mode} handleModeChange={handleModeChange} />
                    <StatsHeader columns={columns} />
                </Menu>
            </Visibility> */}

            {/* ******************************** */}

            <Dimmer.Dimmable
                as={Grid}
                ref={handleContextRef}
                dimmed={loading}
                style={{ backgroundColor: '#2c3c57', color: 'white' }}
            >
                <Dimmer active={loading}>
                    <Loader>Loading...</Loader>
                </Dimmer>

                {players.map((player, index) => (
                    <Player
                        key={index}
                        columns={columns}
                        player={player}
                        mode={mode}
                        boardIndex={index}
                        removePlayer={removePlayer}
                    />
                ))}
            </Dimmer.Dimmable>
        </React.Fragment>
    );
};

export default Index;
