import React, { Component } from 'react';
import { Container, Menu, Icon, Segment, Responsive } from 'semantic-ui-react';
// import { Link } from '../../routes';
import Link from 'next/link';
import SearchBar from './SearchBar';

export default class Navbar extends Component {
    constructor(props) {
        super(props);

        this.state = {
            searchVisible: false
        };
    }

    render() {
        return (
            <Segment
                style={{ color: 'white', padding: '0px' }}
                textAlign="center"
                vertical
                inverted
            >
                <Menu pointing fixed="top" secondary size="large" style={{ color: 'white' }}>
                    <Container style={{ backgroundColor: '#4a4a4a', color: 'white' }}>
                        <Menu.Item active style={{ color: 'white' }}>
                            <Link as="/" href="/">
                                <a>Home</a>
                            </Link>
                        </Menu.Item>
                        <Menu.Item style={{ color: 'white' }}>
                            <Link as="/leaderboard" href="/leaderboard">
                                <span as="a">Leaderboards</span>
                            </Link>
                        </Menu.Item>

                        {/* BARS */}
                        <Responsive
                            as={Menu.Item}
                            icon
                            position="right"
                            style={{ color: 'grey' }}
                            onClick={() => console.log('clicked!')}
                            maxWidth={Responsive.onlyMobile.maxWidth}
                        >
                            <Icon name="bars" size="large" />
                        </Responsive>
                    </Container>
                </Menu>
            </Segment>
        );
    }
}
