import React, { Component } from 'react';
import { bindActionCreators } from 'redux';

import { connect } from 'react-redux';
import _ from 'lodash';
import { Container, Grid, Segment, Card } from 'semantic-ui-react';
import SearchBar from '../src/components/SearchBar';
import NewsCard from '../src/components/NewsCard';

import { fetchByUsername, getNews } from '../src/actions';

class Index extends Component {
    constructor(props) {
        super(props);

        this.state = {
            news: []
        };
    }
    async componentDidMount() {
        const data = await getNews();
        console.log(data.entries);
        this.setState({ news: data.entries });
    }

    handleResultSelect = username => {
        this.props.router.push(`/profile/${username}`);
    };

    prefetchRoute = username => {
        this.props.router.prefetch(`/profile/${username}`);
    };

    render() {
        const { searchResults, fetchByUsername } = this.props;

        // debounced fetch
        const debouncedfetchByUsername = _.debounce(fetchByUsername, 500, {
            maxWait: 1000
        });

        return (
            <Container
                text
                textAlign="center"
                style={{
                    paddingTop: '100px',
                    paddingBottom: '100px',
                    height: '100%'
                }}
            >
                <h1>Fortnite Boards</h1>
                <Grid columns="1" centered textAlign="center">
                    <Grid.Column mobile="16" computer="6" largeScreen="6" tablet="6" widescreen="6">
                        <Container>
                            <SearchBar
                                handleResultSelect={this.handleResultSelect}
                                prefetchRoute={this.prefetchRoute}
                                fetchByUsername={debouncedfetchByUsername}
                                results={searchResults}
                            />
                        </Container>
                    </Grid.Column>
                    <Grid.Column textAlign="center">
                        <Card.Group stackable itemsPerRow={3}>
                            {this.state.news.slice(0, 3).map(article => (
                                <NewsCard
                                    key={article.title}
                                    imagesrc={article.image}
                                    title={article.title}
                                    description={article.body}
                                    date={article.time}
                                />
                            ))}
                        </Card.Group>
                        {/* <Segment
                            padded
                            style={{
                                backgroundColor: 'white',
                                height: '200px'
                            }}
                        >
                            <Card.Group itemsPerRow="3">
                                {this.state.news.map(article => (
                                    <NewsCard
                                        key={article.title}
                                        imagesrc={article.image}
                                        title={article.title}
                                        description={article.body}
                                        date={article.time}
                                    />
                                ))}
                            </Card.Group>
                            <h2>NEWS</h2>
                            <h2>NEWS</h2>
                            <h2>NEWS</h2>
                        </Segment> */}
                    </Grid.Column>
                </Grid>
            </Container>
        );
    }
}

const mapStateToProps = state => {
    return {
        searchResults: state.search.searchResults
    };
};

const mapDispatchToProps = dispatch => bindActionCreators({ fetchByUsername }, dispatch);

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Index);
