import React from 'react';
import { Grid, Icon, Table } from 'semantic-ui-react';

const ModeHeader = ({ mode, handleModeChange }) => {
    let title = mode.charAt(0).toUpperCase() + mode.slice(1);

    return (
        <Grid.Row as={Table.Header} columns="3" className={mode}>
            <Grid.Column
                as={Table.HeaderCell}
                floated="left"
                textAlign="left"
                onClick={() => handleModeChange('reverse')}
            >
                <Icon name="angle left" />
            </Grid.Column>
            <Grid.Column as="span" textAlign="center">
                {title}
            </Grid.Column>
            <Grid.Column
                as="span"
                floated="right"
                textAlign="right"
                onClick={() => handleModeChange('forward')}
            >
                <Icon name="angle right" />
            </Grid.Column>
        </Grid.Row>
    );
};

export default ModeHeader;
