import React from 'react';
import { Container, Menu, Segment, Responsive } from 'semantic-ui-react';

const Navbar = ({ router }) => {
    const changeRoute = path => {
        router.push(router.pathname == path ? router.asPath : path);
    };
    return (
        <Segment
            // style={{ backgroundColor: '#1B2137', color: 'white', padding: '0px' }}
            textAlign="center"
            vertical
        >
            <Menu
                className="Navbar"
                pointing
                fixed="top"
                secondary
                size="large"
                style={{ backgroundColor: '#1B2137', color: 'white' }}
            >
                <Container style={{ backgroundColor: '#1B2137', color: 'white' }}>
                    <Menu.Item active={router.pathname == '/'} onClick={() => changeRoute('/')}>
                        <span as="a">Home</span>
                    </Menu.Item>
                    <Menu.Item
                        active={router.pathname == '/leaderboard'}
                        onClick={() => changeRoute('/leaderboard')}
                    >
                        <span as="a">Leaderboards</span>
                    </Menu.Item>

                    <Responsive
                        as={Menu.Item}
                        icon="bars"
                        fitted="horizontally"
                        position="right"
                        size="large"
                        style={{ color: 'grey' }}
                        maxWidth={Responsive.onlyMobile.maxWidth}
                    />
                </Container>
            </Menu>
        </Segment>
    );
};

export default Navbar;
