import React from 'react';
import { Grid } from 'semantic-ui-react';
import ModeHeader from './ModeHeader';
import StatsHeader from './StatsHeader';
import Player from './Player';

const index = ({ columns, activeColumnSort, players, removePlayer, handleModeChange, mode }) => {
    return (
        <Grid style={{ backgroundColor: '#2c3c57', color: 'white' }}>
            {/* Render Mode Header / Selector */}
            <ModeHeader mode={mode} handleModeChange={handleModeChange} />

            {/* Render Column Header */}
            <StatsHeader columns={columns} activeColumnSort={activeColumnSort} />

            {/* Render Player stats */}
            {players.map((player, index) => (
                <Player
                    columns={columns}
                    player={player}
                    key={index}
                    mode={mode}
                    boardIndex={index}
                    removePlayer={removePlayer}
                />
            ))}
        </Grid>
    );
};

export default index;
