import React from 'react';
import { Grid, Sticky, Rail } from 'semantic-ui-react';
import ModeHeader from './ModeHeader';
import StatsHeader from './StatsHeader';
import Player from './Player';

const index = ({ columns, players, removePlayer, handleModeChange, mode }) => {
    return (
        <Grid style={{ backgroundColor: '#2c3c57', color: 'white' }}>
            <Rail
                internal
                position="left"
                attached
                style={{ top: 'auto', height: 'auto', width: '100%' }}
            >
                <Sticky context={this.state.context2}>
                    <ModeHeader mode={mode} handleModeChange={handleModeChange} />
                    <StatsHeader columns={columns} />
                </Sticky>
            </Rail>

            {/* <ModeHeader mode={mode} handleModeChange={handleModeChange} />
            <StatsHeader columns={columns} /> */}
            {players.map((player, index) => (
                <Player
                    key={index}
                    columns={columns}
                    player={player}
                    mode={mode}
                    boardIndex={index}
                    removePlayer={removePlayer}
                />
            ))}
        </Grid>
    );
};

export default index;
