import _ from 'lodash';
import React, { Component, useState, useEffect } from 'react';
import { Search, Label } from 'semantic-ui-react';
import { getUsernames } from '../actions';

const resultRenderer = ({ uid, username }) => <Label key={uid} content={username} />;

export default class SearchBar extends Component {
    constructor(props) {
        super(props);

        this.state = {
            value: '',
            isLoading: false
        };
    }

    componentWillMount() {
        this.resetComponent();
    }

    resetComponent = () => this.setState({ isLoading: false, value: '' });

    handleResultSelect = async (e, { result }) => {
        const { handleResultSelect } = this.props;
        this.setState({ value: result.username, isLoading: true });
        await handleResultSelect(result.username);
        this.resetComponent();
    };

    handleSearchChange = (e, { value }) => {
        this.setState({ isLoading: true, value }, () => {
            // reset loading if empty
            if (this.state.value.length < 1) return this.resetComponent();

            // fetch users by username
            this.props.fetchByUsername(this.state.value, () => {
                this.setState({ isLoading: false });
            });
        });
    };

    render() {
        const { isLoading, value } = this.state;
        const { results } = this.props;
        return (
            <Search
                input={{ fluid: true, icon: 'arrow circle right' }}
                loading={isLoading}
                placeholder={'Enter your Epic username'}
                onResultSelect={this.handleResultSelect}
                onSearchChange={_.debounce(this.handleSearchChange, 500, {
                    leading: true
                })}
                results={results}
                resultRenderer={resultRenderer}
                value={value}
                showNoResults={false}
            />
        );
    }
}

export const SearchBarr = ({ results, handleResultSelect, fetchByUsername }) => {
    const [state, setState] = useState({ value: '', isLoading: false });
    // const [results, setResults] = useState([]);

    useEffect(() => {
        resetComponent();
    }, []);

    const resetComponent = () => setState({ isLoading: false, value: '' });

    const onResultSelect = async (e, { result }) => {
        setState({ value: result.username, isLoading: true });
        await handleResultSelect(result.username);
        this.resetComponent();
    };

    const onSearchChange = async (e, { value }) => {
        setState({ isLoading: true, value: value });

        const results = await getUsernames(value);
        const formattedResults = results.map((result, index) => {
            result.key = index;
            result.title = result.username;
            return result;
        });

        setState({ ...state, isLoading: false });
    };

    const debouncedSearchChange = _.debounce(onSearchChange, 500, {
        maxWait: 1000
    });

    return (
        <Search
            input={{ fluid: true, icon: 'arrow circle right' }}
            loading={state.isLoading}
            placeholder={'Enter your Epic username'}
            onResultSelect={onResultSelect}
            onSearchChange={_.debounce(onSearchChange, 500, {
                leading: true
            })}
            results={results}
            resultRenderer={resultRenderer}
            value={state.value}
            showNoResults={false}
        />
    );
};
