import React, { Component } from 'react';
import { Container, Divider } from 'semantic-ui-react';

import { getStatsByUsername, getProfileByUsername, getMatchesByUid } from '../src/actions';

import StatsTable from '../src/components/Profile/StatsTable';
import TotalStatsTable from '../src/components/Profile/TotalStatsTable';

class Profile extends Component {
    static async getInitialProps({ query }) {
        const profile = (await getProfileByUsername(query.username)) || {};
        if (!profile.uid) {
            const err = new Error();
            err.code = 'ENOENT';
            throw err;
        }
        return { profile };
    }

    constructor(props) {
        super(props);
    }

    async componentDidMount() {
        const profile = await getProfileByUsername(this.props.profile.username);
        console.log(profile);
    }

    render() {
        const { profile, matches } = this.props.profile;
        console.log(profile);
        const { stats, totals } = profile;
        const season = profile.window.charAt(profile.window.length - 1);

