import React from 'react';
import { Grid, Header, Table } from 'semantic-ui-react';

const StatsHeader = ({ columns, activeColumnSort, sortPlayers }) => {
    return (
        <Grid.Row as={Table.Header} columns={columns.length} stretched>
            {/* Rank header */}
            <Grid.Column
                textAlign="center"
                verticalAlign="middle"
                mobile="1"
                onClick={() => {
                    sortPlayers('#');
                }}
            >
                <Header size="tiny">
                    <Header.Subheader
                        as="h6"
                        style={{ color: activeColumnSort == '#' ? 'yellow' : '#92a2bd' }}
                    >
                        #
                    </Header.Subheader>
                </Header>
            </Grid.Column>

            {/* Stats headers */}
            {columns.map((column, index) => (
                <Grid.Column
                    as={Table.HeaderCell}
                    className={index == 0 ? '' : 'compact'}
                    key={index}
                    onClick={() => {
                        sortPlayers(column.header);
                    }}
                    textAlign={index == 0 ? 'left' : 'center'}
                    verticalAlign="middle"
                    mobile={column.size}
                >
                    <Header size="tiny">
                        <Header.Subheader
                            as="h6"
                            style={{
                                color: activeColumnSort == column.header ? 'yellow' : '#92a2bd'
                            }}
                        >
                            {column.header}
                        </Header.Subheader>
                    </Header>
                </Grid.Column>
            ))}
        </Grid.Row>
    );
};

export default StatsHeader;
