import React, { Component } from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import _ from 'lodash';

import { Container, Grid, Table, Icon, Header } from 'semantic-ui-react';
import SearchBar from '../src/components/SearchBar';
import { fetchByUsername, getProfile } from '../src/actions';

import Board from '../src/components/board';

// show awards, i.e. most kills, most wins, highest kd etc etc in separate div

class Leaderboard extends Component {
    static async getInitialProps({ store, query, pathname, asPath, req }) {
        const { usernames, entries } = query;
        // spread into array for mapping
        let initialPlayers = [];

        if (!usernames) return { initialPlayers };

        if (entries <= 1) {
            initialPlayers = await getProfile(usernames);
        } else {
            initialPlayers = await Promise.all(usernames.map(username => getProfile(username)));
        }
        return { initialPlayers, entries };
    }

    constructor(props) {
        super(props);
        this.state = {
            players: [],
            mode: 'solo',
            columns: [],
            activeColumnSort: '#',
            column: null,
            direction: null
        };
    }

    handleSort = (sortFn, clickedColumn) => () => {
        const { column, players, direction, mode } = this.state;
        if (column !== clickedColumn) {
            this.setState(
                {
                    column: clickedColumn,
                    players: _.sortBy(players, o => o.stats[`winrate_${mode}`]),
                    direction: 'ascending'
                },
                () => this.updateURL()
            );

            return;
        }

        this.setState(
            {
                players: players.reverse(),
                direction: direction === 'ascending' ? 'descending' : 'ascending'
            },
            () => this.updateURL()
        );
    };

    componentDidMount() {
        const { initialPlayers, entries, mode } = this.props;
        const { players } = this.state;
        this.setState({
            columns: [
                {
                    header: 'Player',
                    accessor: 'username',
                    size: '5',
                    sort: this.handleSort(
                        () => _.sortBy(this.state.players, o => o.username),
                        'username'
                    )
                },
                {
                    header: 'K/D',
                    accessor: 'kd',
                    size: '2',
                    sort: this.handleSort(
                        () => _.sortBy(this.state.players, o => o.stats[`kd_${mode}`]),
                        'K/D'
                    )
                },
                {
                    header: 'Win %',
                    accessor: 'winrate',
                    size: '2',
                    sort: this.handleSort('winrate')
                },
                {
                    header: 'Kills',
                    accessor: 'kills',
                    size: '2',
                    sort: this.handleSort(
                        () => _.sortBy(this.state.players, o => o.stats[`kills_${mode}`]),
                        'Kills'
                    )
                },
                {
                    header: 'Wins',
                    accessor: 'placetop1',
                    size: '2',
                    sort: this.handleSort(
                        () => _.sortBy(this.state.players, o => o.stats[`placetop1_${mode}`]),
                        'Wins'
                    )
                }
            ],
            players: entries <= 1 ? [...players, initialPlayers] : [...players, ...initialPlayers],
            entries: entries
        });
    }

    addPlayer = async username => {
        const { players } = this.state;

        const newPlayer = await getProfile(username);
        const updatedPlayers = [...players, newPlayer];
        this.setState({ players: updatedPlayers }, () => this.updateURL());
    };

    removePlayer = index => {
        const { players } = this.state;
        const updatedPlayers = players.filter((player, i) => i !== index);
        this.setState({ players: updatedPlayers }, () => this.updateURL());
    };

    sortPlayers = columnHeader => {
        this.setState({ activeColumnSort: columnHeader });
        console.log(this.state.players);
    };

    changeMode = direction => {
        const { mode } = this.state;
        let nextMode = '';
        switch (mode) {
            case 'solo':
                nextMode = direction === 'forward' ? 'duo' : 'squad';
                break;
            case 'duo':
                nextMode = direction === 'forward' ? 'squad' : 'solo';
                break;
            case 'squad':
                nextMode = direction === 'forward' ? 'solo' : 'duo';
                break;
            default:
                break;
        }
        this.setState({ mode: nextMode });
    };

    isPlayerSelected = username => {
        const { players } = this.state;
        return players.find(player => player.username === username);
    };

    updateURL = () => {
        const { router } = this.props;
        const { players } = this.state;
        const usernames = players.map(player => player.username);
        router.push({
            pathname: '/leaderboard',
            query: { entries: usernames.length, usernames: usernames },
            options: { shallow: true }
        });
    };

    render() {
        const { columns, mode, players, activeColumnSort, column, direction } = this.state;
        const { searchResults, fetchByUsername } = this.props;
        const debouncedfetchByUsername = _.debounce(fetchByUsername, 500, {
            maxWait: 1000
        });
        return (
            <div>
                <Container
                    text
                    textAlign="center"
                    style={{
                        paddingTop: '200px',
                        paddingBottom: '100px',
                        height: '100%'
                    }}
                >
                    <h1>Board</h1>
                    <Grid columns="1" centered textAlign="center">
                        <Grid.Column
                            mobile="16"
                            computer="6"
                            largeScreen="6"
                            tablet="6"
                            widescreen="6"
                        >
                            <Container>
                                <SearchBar
                                    checkDuplicateSelect={this.isPlayerSelected}
                                    handleResultSelect={this.addPlayer}
                                    fetchByUsername={debouncedfetchByUsername}
                                    results={searchResults}
                                />
                            </Container>
                        </Grid.Column>
                        <Grid.Column textAlign="center">
                            <Board
                                columns={columns}
                                sortPlayers={this.sortPlayers}
                                activeColumnSort={activeColumnSort}
                                mode={mode}
                                players={players}
                                removePlayer={this.removePlayer}
                                handleModeChange={this.changeMode}
                            />
                        </Grid.Column>
                        {/*  */}
                        {/*  */}
                        {/*  */}
                        {/*  */}
                        {/*  */}
                        {/*  */}
                        {/*  */}
                        {/*  */}
                        {/*  */}
                        {/*  */}
                        {/*  */}
                        {/*  */}
                        {/*  */}
                        {/*  */}
                        {/*  */}
                        <Grid.Column textAlign="center">
                            <Table
                                sortable
                                unstackable
                                compact
                                size="small"
                                style={{ backgroundColor: '#2c3c57', color: 'white' }}
                            >
                                <Table.Header>
                                    <Table.Row>
                                        <Table.HeaderCell
                                            textAlign="center"
                                            style={{ backgroundColor: '#2c3c57', color: 'white' }}
                                        >
                                            <Header size="tiny">
                                                <Header.Subheader
                                                    as="h6"
                                                    style={{
                                                        color: '#92a2bd'
                                                    }}
                                                >
                                                    #
                                                </Header.Subheader>
                                            </Header>{' '}
                                        </Table.HeaderCell>
                                        <Table.HeaderCell
                                            style={{ backgroundColor: '#2c3c57', color: 'white' }}
                                            // sorted={column === 'username' ? direction : null}
                                            onClick={this.handleSort(
                                                () => _.sortBy(players, o => o.username),
                                                'username'
                                            )}
                                        >
                                            <Header size="tiny">
                                                <Header.Subheader
                                                    as="h6"
                                                    style={{
                                                        color: '#92a2bd'
                                                    }}
                                                >
                                                    Player
                                                </Header.Subheader>
                                            </Header>
                                        </Table.HeaderCell>
                                        <Table.HeaderCell
                                            textAlign="center"
                                            style={{ backgroundColor: '#2c3c57', color: 'white' }}
                                            // sorted={column === 'kd' ? direction : null}
                                            onClick={this.handleSort(
                                                () => _.sortBy(players, o => o.stats[`kd_${mode}`]),
                                                'kd'
                                            )}
                                        >
                                            <Header size="tiny">
                                                <Header.Subheader
                                                    as="h6"
                                                    style={{
                                                        color: '#92a2bd'
                                                    }}
                                                >
                                                    K/D
                                                </Header.Subheader>
                                            </Header>
                                        </Table.HeaderCell>
                                        <Table.HeaderCell
                                            textAlign="center"
                                            style={{ backgroundColor: '#2c3c57', color: 'white' }}
                                            // sorted={column === 'winrate' ? direction : null}
                                            onClick={this.handleSort(
                                                () =>
                                                    _.sortBy(
                                                        players,
                                                        o => o.stats[`winrate_${mode}`]
                                                    ),
                                                'winrate'
                                            )}
                                        >
                                            <Header size="tiny">
                                                <Header.Subheader
                                                    as="h6"
                                                    style={{
                                                        color: '#92a2bd'
                                                    }}
                                                >
                                                    Win %
                                                </Header.Subheader>
                                            </Header>
                                        </Table.HeaderCell>
                                        <Table.HeaderCell
                                            textAlign="center"
                                            style={{ backgroundColor: '#2c3c57', color: 'white' }}
                                            // sorted={column === 'kills' ? direction : null}
                                            onClick={this.handleSort(
                                                () =>
                                                    _.sortBy(
                                                        players,
                                                        o => o.stats[`kills_${mode}`]
                                                    ),
                                                'kills'
                                            )}
                                        >
                                            <Header size="tiny">
                                                <Header.Subheader
                                                    as="h6"
                                                    style={{
                                                        color: '#92a2bd'
                                                    }}
                                                >
                                                    Kills
                                                </Header.Subheader>
                                            </Header>
                                        </Table.HeaderCell>
                                        <Table.HeaderCell
                                            textAlign="center"
                                            style={{ backgroundColor: '#2c3c57', color: 'white' }}
                                            // sorted={column === 'wins' ? direction : null}
                                            onClick={this.handleSort(
                                                () =>
                                                    _.sortBy(
                                                        players,
                                                        o => o.stats[`placetop1_${mode}`]
                                                    ),
                                                'wins'
                                            )}
                                        >
                                            <Header size="tiny">
                                                <Header.Subheader
                                                    as="h6"
                                                    style={{
                                                        color: '#92a2bd'
                                                    }}
                                                >
                                                    Wins
                                                </Header.Subheader>
                                            </Header>
                                        </Table.HeaderCell>
                                    </Table.Row>
                                </Table.Header>
                                <Table.Body>
                                    {_.map(players, (player, index) => (
                                        <Table.Row key={player.username} verticalAlign="top">
                                            <Table.Cell>
                                                <Header as="h6" style={{ color: '#92a2bd' }}>
                                                    {index + 1}
                                                </Header>
                                            </Table.Cell>
                                            <Table.Cell singleLine>
                                                <Header as="h6" style={{ color: 'white' }}>
                                                    {player.username}

                                                    <Header.Subheader
                                                        size="small"
                                                        style={{ color: '#92a2bd' }}
                                                    >
                                                        {player.stats[`matchesplayed_${mode}`]}{' '}
                                                        Matches
                                                    </Header.Subheader>
                                                </Header>
                                            </Table.Cell>
                                            <Table.Cell singleLine textAlign="center">
                                                <Header as="h4" style={{ color: 'white' }}>
                                                    {player.stats[`kd_${mode}`]}
                                                </Header>
                                            </Table.Cell>
                                            <Table.Cell singleLine textAlign="center">
                                                <Header as="h4" style={{ color: 'white' }}>
                                                    {player.stats[`winrate_${mode}`]}
                                                </Header>
                                            </Table.Cell>
                                            <Table.Cell singleLine textAlign="center">
                                                <Header as="h4" style={{ color: 'white' }}>
                                                    {player.stats[`kills_${mode}`]}
                                                </Header>
                                            </Table.Cell>
                                            <Table.Cell collapsing singleLine textAlign="center">
                                                <Header as="h4" style={{ color: 'white' }}>
                                                    {player.stats[`placetop1_${mode}`]}
                                                </Header>
                                            </Table.Cell>
                                            <Table.Cell
                                                singleLine
                                                textAlign="center"
                                                icon="trash alternate outline"
                                                style={{
                                                    color: 'red',
                                                    fontSize: '12px',
                                                    paddingLeft: '0px'
                                                }}
                                            />
                                        </Table.Row>
                                    ))}
                                </Table.Body>
                            </Table>
                        </Grid.Column>
                    </Grid>
                </Container>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        searchResults: state.search.searchResults
    };
};

const mapDispatchToProps = dispatch => bindActionCreators({ fetchByUsername }, dispatch);

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Leaderboard);
