                            size="small"
                            style={{ backgroundColor: '#2c3c57', color: 'white' }}
                        >
                            <Table.Header>
                                <Table.Row>
                                    <Table.HeaderCell
                                        textAlign="center"
                                        style={{ backgroundColor: '#2c3c57', color: 'white' }}
