                                        <Header size="tiny">
                                            <Header.Subheader
                                                as="h6"
                                                style={{
                                                    color: '#92a2bd'
                                                }}
                                            >
                                                #
                                            </Header.Subheader>
                                        </Header>{' '}
                                    </Table.HeaderCell>
                                    <Table.HeaderCell
                                        style={{ backgroundColor: '#2c3c57', color: 'white' }}
                                        // sorted={column === 'username' ? direction : null}
                                        onClick={this.handleSort(
                                            () => _.sortBy(players, o => o.username),
                                            'username'
                                        )}
                                    >
                                        <Header size="tiny">
                                            <Header.Subheader
                                                as="h6"
