                                        style={{ backgroundColor: '#2c3c57', color: 'white' }}
                                        // sorted={column === 'kd' ? direction : null}
                                        onClick={this.handleSort(
                                            () => _.sortBy(players, o => o.stats[`kd_${mode}`]),
                                            'kd'
                                        )}
                                    >
                                        <Header size="tiny">
                                            <Header.Subheader
                                                as="h6"
                                                style={{
                                                    color: '#92a2bd'
                                                }}
                                            >
                                                K/D
                                            </Header.Subheader>
                                        </Header>
                                    </Table.HeaderCell>
                                    <Table.HeaderCell
                                        textAlign="center"
                                        style={{ backgroundColor: '#2c3c57', color: 'white' }}
                                        // sorted={column === 'winrate' ? direction : null}
                                        onClick={this.handleSort(
                                            () =>
                                                _.sortBy(players, o => o.stats[`winrate_${mode}`]),
                                            'winrate'
                                        )}
                                    >
                                        <Header size="tiny">
                                            <Header.Subheader
                                                as="h6"
                                                style={{
                                                    color: '#92a2bd'
                                                }}
                                            >
                                                Win %
                                            </Header.Subheader>
                                        </Header>
                                    </Table.HeaderCell>
                                    <Table.HeaderCell
                                        textAlign="center"
                                        style={{ backgroundColor: '#2c3c57', color: 'white' }}
                                        // sorted={column === 'kills' ? direction : null}
                                        onClick={this.handleSort(
                                            () => _.sortBy(players, o => o.stats[`kills_${mode}`]),
                                            'kills'
                                        )}
                                    >
                                        <Header size="tiny">
                                            <Header.Subheader
                                                as="h6"
                                                style={{
                                                    color: '#92a2bd'
                                                }}
                                            >
                                                Kills
                                            </Header.Subheader>
                                        </Header>
                                    </Table.HeaderCell>
                                    <Table.HeaderCell
                                        textAlign="center"
                                        style={{ backgroundColor: '#2c3c57', color: 'white' }}
                                        // sorted={column === 'wins' ? direction : null}
                                        onClick={this.handleSort(
                                            () =>
                                                _.sortBy(
                                                    players,
                                                    o => o.stats[`placetop1_${mode}`]
                                                ),
                                            'wins'
                                        )}
                                    >
                                        <Header size="tiny">
                                            <Header.Subheader
                                                as="h6"
                                                style={{
                                                    color: '#92a2bd'
                                                }}
                                            >
                                                Wins
                                            </Header.Subheader>
                                        </Header>
                                    </Table.HeaderCell>
                                </Table.Row>
                            </Table.Header>
                            <Table.Body>
                                {_.map(players, (player, index) => (
                                    <Table.Row key={player.username} verticalAlign="top">
                                        <Table.Cell>
                                            <Header as="h6" style={{ color: '#92a2bd' }}>
                                                {index + 1}
                                            </Header>
                                        </Table.Cell>
                                        <Table.Cell singleLine>
                                            <Header as="h6" style={{ color: 'white' }}>
                                                {player.username}

                                                <Header.Subheader
                                                    size="small"
                                                    style={{ color: '#92a2bd' }}
                                                >
                                                    {player.stats[`matchesplayed_${mode}`]} Matches
                                                </Header.Subheader>
                                            </Header>
                                        </Table.Cell>
                                        <Table.Cell singleLine textAlign="center">
                                            <Header as="h4" style={{ color: 'white' }}>
