import React from 'react';
import { Grid, Dimmer, Loader } from 'semantic-ui-react';
import ModeHeader from './ModeHeader';
import StatsHeader from './StatsHeader';
import Player from './Player';

const Index = ({
    activeColumn,
    columns,
    players,
    removePlayer,
    handleModeChange,
    mode,
    loading
}) => {
    return (
        <div style={{ minHeight: '600px', backgroundColor: 'rgba(61, 74, 133, 0.3)' }}>
            <Grid
                style={{
                    backgroundColor: 'rgba(61, 74, 133, 0.3)',
                    color: 'white'
                }}
            >
                <ModeHeader mode={mode} handleModeChange={handleModeChange} />

                <StatsHeader activeColumn={activeColumn} columns={columns} loading={loading} />

                <Dimmer.Dimmable
                    as={Grid.Row}
                    dimmed={loading}
                    style={{
                        backgroundColor: '#2c3c57',
                        color: 'white',
                        padding: '0',
                        margin: '0'
                    }}
                >
                    <Dimmer active={loading}>
                        <Loader>Loading...</Loader>
                    </Dimmer>
                    <Grid style={{ width: '100%', margin: '0', padding: '0' }}>
                        {players.map((player, index) => (
                            <Player
                                key={index}
                                columns={columns}
                                player={player}
                                mode={mode}
                                boardIndex={index}
                                removePlayer={removePlayer}
                            />
                        ))}
                    </Grid>
                </Dimmer.Dimmable>
            </Grid>
        </div>
    );
};

export default Index;

