import React from 'react';
import { Grid, Header, Table } from 'semantic-ui-react';

const StatsHeader = ({ columns, activeColumnSort, sortPlayers }) => {
    return (
        <Grid.Row columns={columns.length} stretched>
            {/* Rank header */}
            <Grid.Column
                style={{ backgroundColor: activeColumnSort == '#' ? 'black' : '#2c3c57' }}
                textAlign="center"
                verticalAlign="middle"
                mobile="1"
                onClick={() => {
                    sortPlayers('#');
                }}
            >
                <Header size="tiny">
                    <Header.Subheader
                        as="h6"
                        style={{
                            color: activeColumnSort == '#' ? 'yellow' : '#92a2bd'
                        }}
                    >
                        #
                    </Header.Subheader>
                </Header>
            </Grid.Column>

            {/* Stats headers */}
            {/* {columns.map((column, index) => (
                <Grid.Column
                    style={{
                        backgroundColor: activeColumnSort == column.header ? 'black' : '#2c3c57'
                    }}
                    className={index == 0 ? '' : 'compact'}
                    key={index}
                    onClick={() => {
                        sortPlayers(column.header);
                    }}
                    textAlign={index == 0 ? 'left' : 'center'}
                    verticalAlign="middle"
                    mobile={column.size}
                >
                    <Header size="tiny">
                        <Header.Subheader
                            as="h6"
                            style={{
                                color: activeColumnSort == column.header ? 'yellow' : '#92a2bd'
                            }}
                        >
                            {column.header}
                        </Header.Subheader>
                    </Header>
                </Grid.Column>
            ))} */}
            <Grid.Column
                style={{ backgroundColor: '#2c3c57' }}
                as={Table.HeaderCell}
                textAlign="left"
                verticalAlign="middle"
                mobile="5"
            >
                <Header size="tiny">
                    <Header.Subheader
                        as="h6"
                        style={{
                            color: '#92a2bd'
                        }}
                    >
                        Player
                    </Header.Subheader>
                </Header>
            </Grid.Column>
            <Grid.Column
                style={{ backgroundColor: '#2c3c57' }}
                as={Table.HeaderCell}
                className="compact"
                textAlign="center"
                verticalAlign="middle"
                mobile="2"
            >
                <Header size="tiny">
                    <Header.Subheader
                        as="h6"
                        style={{
                            color: '#92a2bd'
                        }}
                    >
                        K/D
                    </Header.Subheader>
                </Header>
            </Grid.Column>
            <Grid.Column
                style={{ backgroundColor: '#2c3c57' }}
                as={Table.HeaderCell}
                className="compact"
                textAlign="center"
                verticalAlign="middle"
                mobile="2"
            >
                <Header size="tiny">
                    <Header.Subheader
                        as="h6"
                        style={{
                            color: '#92a2bd'
                        }}
                    >
                        Win %
                    </Header.Subheader>
                </Header>
            </Grid.Column>
            <Grid.Column
                style={{ backgroundColor: '#2c3c57' }}
                as={Table.HeaderCell}
                className="compact"
                textAlign="center"
                verticalAlign="middle"
                mobile="2"
            >
                <Header size="tiny">
                    <Header.Subheader
                        as="h6"
                        style={{
                            color: '#92a2bd'
                        }}
                    >
                        Kills
                    </Header.Subheader>
                </Header>
            </Grid.Column>
            <Grid.Column
                style={{ backgroundColor: '#2c3c57' }}
                as={Table.HeaderCell}
                className="compact"
                textAlign="center"
                verticalAlign="middle"
                mobile="2"
            >
                <Header size="tiny">
                    <Header.Subheader
                        as="h6"
                        style={{
                            color: '#92a2bd'
                        }}
                    >
                        Wins
                    </Header.Subheader>
                </Header>
            </Grid.Column>
        </Grid.Row>
    );
};

export default StatsHeader;
