import React from 'react';
import { Grid, Header } from 'semantic-ui-react';

const Player = ({ columns, mode, player, index }) => {
    return (
        <Grid.Row key={index} columns={columns.length} stretched>
            {columns.map((column, columnIndex) => {
                const columnContent =
                    columnIndex === 0 // if first column
                        ? player.username // get username
                        : player.stats[`${column.accessor}_${mode}`]; // else get stats
                return (
                    <Grid.Column
                        key={columnIndex}
                        textAlign={columnIndex == 0 ? 'left' : 'center'}
                        verticalAlign="middle"
                        mobile={
                            columnIndex == 0
                                ? '4'
                                : columnIndex == 2
                                ? '2.5'
                                : columnIndex == 4
                                ? '3.5'
                                : columnIndex == 5
                                ? '3.5'
                                : '2'
                        }
                    >
                        <Header as="h6" style={{ color: 'white' }}>
                            {columnContent}
                        </Header>
                    </Grid.Column>
                );
            })}
        </Grid.Row>
    );
};

export default Player;
