import React from 'react';
import { Grid, Sticky } from 'semantic-ui-react';
import ModeHeader from './ModeHeader';
import StatsHeader from './StatsHeader';
import Player from './Player';

const Index = ({ columns, players, removePlayer, handleModeChange, mode, contextRef }) => {
    return (
        <Grid style={{ backgroundColor: '#2c3c57', color: 'white' }}>
            <Sticky context={contextRef} pushing style={{ width: '100%' }}>
                <Grid style={{ backgroundColor: '#2c3c57', color: 'white', height: '200px' }}>
                    <ModeHeader mode={mode} handleModeChange={handleModeChange} />
                    <StatsHeader columns={columns} />
                </Grid>
            </Sticky>
            {players.map((player, index) => (
                <Player
                    key={index}
                    columns={columns}
                    player={player}
                    mode={mode}
                    boardIndex={index}
                    removePlayer={removePlayer}
                />
            ))}
        </Grid>
    );
};

export default Index;
