import React from 'react';
import { Grid, Header, Icon } from 'semantic-ui-react';

const Player = ({ columns, mode, player, boardIndex, removePlayer }) => {
    return (
        <Grid.Row columns={columns.length} stretched>
            {/* Rank column */}
            <Grid.Column as={TableCell} textAlign="center" mobile="1">
                <Header as="h6" style={{ color: '#92a2bd' }}>
                    {boardIndex + 1}
                </Header>
            </Grid.Column>

            {/* Player - K/D - Winrate - Kills - Wins columns */}
            {columns.map((column, columnIndex) => {
                const columnContent =
                    columnIndex === 0 // if first column
                        ? player.username // get username
                        : player.stats[`${column.accessor}_${mode}`]; // else get stats
                return (
                    <Grid.Column
                        as={TableCell}
                        className={columnIndex == 0 ? '' : 'compact'}
                        key={columnIndex}
                        textAlign={columnIndex == 0 ? 'left' : 'center'}
                        mobile={column.size}
                    >
                        <Header as={columnIndex == 0 ? 'h6' : 'h4'} style={{ color: 'white' }}>
                            {columnContent}

                            {/* if Player column, add matches count */}
                            {columnIndex == 0 ? (
                                <Header.Subheader
                                    size="small"
                                    style={{ color: '#92a2bd', whiteSpace: 'nowrap' }}
                                >
                                    {player.stats[`matchesplayed_${mode}`]} Matches
                                </Header.Subheader>
                            ) : null}
                        </Header>
                    </Grid.Column>
                );
            })}

            {/* Delete icon column */}
            <Grid.Column
                textAlign="center"
                // verticalAlign="middle"
                mobile="1"
                onClick={() => {
                    removePlayer(boardIndex);
                }}
            >
                <Icon size="small" color="red" name="trash alternate outline" />
            </Grid.Column>
        </Grid.Row>
    );
};

export default Player;
