import React from 'react';
import { Grid, Rail, Sticky } from 'semantic-ui-react';
import ModeHeader from './ModeHeader';
import StatsHeader from './StatsHeader';
import Player from './Player';

const index = ({ columns, players, removePlayer, handleModeChange, mode }) => {
    return (
        <Grid style={{ backgroundColor: '#2c3c57', color: 'white' }}>
            <ModeHeader mode={mode} handleModeChange={handleModeChange} />
            <StatsHeader columns={columns} />
            {players.map((player, index) => (
                <Player
                    key={index}
                    columns={columns}
                    player={player}
                    mode={mode}
                    boardIndex={index}
                    removePlayer={removePlayer}
                />
            ))}
        </Grid>
    );
};

export default index;

class board extends React.Component {
    state = {};

    handleContextRef = contextRef => this.setState({ contextRef });
    render() {
        const { contextRef } = this.state;
        const { columns, players, removePlayer, handleModeChange, mode } = this.props;
        return (
            <Grid
                ref={this.handleContextRef}
                style={{ backgroundColor: '#2c3c57', color: 'white' }}
            >
                {/* <Rail position="left" internal attached> */}
                <Sticky context={contextRef} pushing>
                    <ModeHeader mode={mode} handleModeChange={handleModeChange} />
                </Sticky>
                {/* </Rail> */}

                <StatsHeader columns={columns} />
                {players.map((player, index) => (
                    <Player
                        key={index}
                        columns={columns}
                        player={player}
                        mode={mode}
                        boardIndex={index}
                        removePlayer={removePlayer}
                    />
                ))}
            </Grid>
        );
    }
}
